SET SQL_MODE="NO_AUTO_VALUE_ON_ZERO";
SET time_zone = "+00:00";

CREATE TABLE `Personen` (
  `ID` mediumint(3) NOT NULL AUTO_INCREMENT,
  `Nachname` varchar(30) COLLATE utf8_unicode_ci NOT NULL,
  `Vorname` varchar(30) COLLATE utf8_unicode_ci NOT NULL,
  `Geschl` varchar(2) COLLATE utf8_unicode_ci NOT NULL,
  `Strasse` varchar(50) COLLATE utf8_unicode_ci NOT NULL,
  `Nr` varchar(6) COLLATE utf8_unicode_ci NOT NULL,
  `PLZ` mediumint(4) NOT NULL,
  `Ort` varchar(45) COLLATE utf8_unicode_ci NOT NULL,
  `Groesse` smallint(3) NOT NULL,
  `Haarfarbe` varchar(20) COLLATE utf8_unicode_ci NOT NULL,
  `Augenfarbe` varchar(20) COLLATE utf8_unicode_ci NOT NULL,
  `Gewicht` smallint(3) NOT NULL,
  `Hobbys` varchar(150) COLLATE utf8_unicode_ci DEFAULT NULL,
  `Besonderheiten` varchar(200) COLLATE utf8_unicode_ci DEFAULT NULL,
  `Alt` smallint(3) NOT NULL,
  PRIMARY KEY (`ID`)
) ENGINE=MyISAM  DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci AUTO_INCREMENT=201 ;

